example.code:
 //Open Outlook Message files
 MapiMessage msg = MapiMessage.FromFile(@"c:\outlookmessage.msg");

//read subject
Console.WriteLine("Subject:" + msg.Subject);

//sender name
Console.WriteLine("From:" + msg.SenderName);

//message body
Console.WriteLine("Body:" + msg.Body);

//Attachments
foreach(MapiAttachment att in msg.Attachments)
{
    Console.WriteLine("Attachment Name:"+att.FileName);
    att.Save(att.FileName);
}

//Remove attachments from Outlook Message files
MapiAttachmentCollection attachments = MapiMessage.RemoveAttachments(@"c:\outlookmessage.msg");

//Attachments
foreach(MapiAttachment att in attachments)
{
   Console.WriteLine("Attachment Name:"+att.FileName);
   att.Save(att.FileName);
}

static object GetSignature(object[] args)
{
    return "John Smith<br>Product Lead<br>Aspose Ltd.<br>" + DateTime.Now.ToShortDateString();
}
static void Main(string[] args)
{
    //create a new MailMessage instance as a template
    MailMessage template = new MailMessage();

    //add template field to subject
    template.Subject = "Hello, #FirstName#";
    template.From = "sale@aspose.com";

    //add template field to receipt
    template.To.Add(new MailAddress("#Receipt#", true));

    //add template field to html body
    //use GetSignment as the template routine, which will provide the same signment.
    template.HtmlBody = "Dear #FirstName# #LastName#, <br><br>Thank you for your interest in <STRONG>Aspose.Email</STRONG>.<br><br>Have fun with it.<br><br>#GetSignature()#";

    //create a new TemplateEngine with the template message.
    TemplateEngine engine = new TemplateEngine(template);

    //register the GetSignment as a templet routine, for we use it in the template.
    engine.RegisterRoutine("GetSignature", new TemplateRoutine(GetSignature));

    //fill a DataTable as data source
    DataTable dt = new DataTable();                        
    dt.Columns.Add("Receipt", typeof(string));
    dt.Columns.Add("FirstName", typeof(string));
    dt.Columns.Add("LastName", typeof(string));          

    DataRow dr;
    dr = dt.NewRow();
    dr["Receipt"] = "Nancy.Davolio<Nancy@somedomain.com>";
    dr["FirstName"] = "Nancy";
    dr["LastName"] = "Davolio";
    dt.Rows.Add(dr);
    dr = dt.NewRow();
    dr["Receipt"] = "Andrew.Fuller<Andrew@somedomain.com>";
    dr["FirstName"] = "Andrew";
    dr["LastName"] = "Fuller";
    dt.Rows.Add(dr);
    dr = dt.NewRow();
    dr["Receipt"] = "Janet.Leverling<Janet@somedomain.com>";
    dr["FirstName"] = "Janet";
    dr["LastName"] = "Leverling";          
     dt.Rows.Add(dr);

     MailMessageCollection messages;
     try
     {
        //create the messages from the template and datasource.
          messages= engine.Instantiate(dt);
     }
     catch (MailException ex)
     {
         System.Diagnostics.Debug.Write(ex.ToString());
    }
}

//Defined a function for processing your field(s)
public object ToUpper(object[] args)
{
    string name = (string)args[0];
    return name.ToUpper();
}

